mapper.select:
select jdh.*, s.supplier OrganName, u.username userName, a.name AccountName
from (select dh.id
from t_depot_head dh
left join t_depot_item di on dh.id = di.header_id and ifnull(di.delete_flag,'0') !='1'
left join t_material m on di.material_id = m.id and ifnull(m.delete_flag,'0') !='1'
left join t_material_extend me on di.material_extend_id = me.id and ifnull(me.delete_flag,'0') !='1'
where 1=1

    and dh.type=#{type}


    and dh.sub_type=#{subType}


    and dh.status in (

        #{status}

    )


    and dh.purchase_status in (

        #{purchaseStatus}

    )



    and dh.number like #{bindNumber}



    and dh.link_number like #{bindLinkNumber}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}



    and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like
    #{bindKey}
    or m.color like #{bindKey} or m.mfrs like #{bindKey} or m.other_field1 like #{bindKey}
    or m.other_field2 like #{bindKey} or m.other_field3 like #{bindKey})


    and dh.organ_id=#{organId}


    and dh.organ_id in (

        #{organId}

    )


    and dh.creator=#{creator}


    and di.depot_id=#{depotId}


    and di.depot_id in (

        #{depotId}

    )


    and dh.creator in (

        #{creator}

    )



    and (dh.account_id=#{accountId} or dh.account_id_list like #{bindAccountId})


    and
    (ifnull(dh.discount_last_money,0)+ifnull(dh.other_money,0)-ifnull(dh.deposit,0)-abs(ifnull(dh.change_amount,0))>0)


    and
    (ifnull(dh.discount_last_money,0)+ifnull(dh.other_money,0)-ifnull(dh.deposit,0)-abs(ifnull(dh.change_amount,0))=0)



    and (dh.remark like #{bindRemark} or di.remark like #{bindRemark})

and ifnull(dh.delete_flag,'0') !='1'
group by dh.id
order by dh.id desc

    limit #{offset},#{rows}
) tb
left join t_depot_head jdh on jdh.id=tb.id and ifnull(jdh.delete_flag,'0') !='1'
left join t_supplier s on jdh.organ_id=s.id and ifnull(s.delete_flag,'0') !='1'
left join t_user u on jdh.creator=u.id and ifnull(u.Status,'0') !='1'
left join t_account a on jdh.account_id=a.id and ifnull(a.delete_flag,'0') !='1'
order by jdh.id desc

