select dh.number,me.bar_code barCode, m.name mname,m.model,m.standard,di.unit_price unitPrice,di.material_unit as mUnit,
di.oper_number operNumber,di.all_price allPrice, ifnull(di.tax_rate,0) taxRate, ifnull(di.tax_money,0) taxMoney, ifnull(di.tax_last_money,0) taxLastMoney,
s.supplier sname,d.dname dname, date_format(dh.oper_time, '%Y-%m-%d') operTime, concat(dh.sub_type,dh.type) as newType,
concat_ws('  ',dh.remark,di.remark) as newRemark
from t_depot_head dh
left join t_depot_item di on di.header_id=dh.id and ifnull(di.delete_flag,'0') !='1'
left join t_material m on m.id=di.material_id and ifnull(m.delete_flag,'0') !='1'
left join t_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1'
left join t_supplier s on s.id=dh.organ_id and ifnull(s.delete_Flag,'0') !='1'
left join (select id,name as dName,delete_Flag from t_depot ) d on d.id=di.depot_id and ifnull(d.delete_Flag,'0') !='1'
where dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}

    and dh.organ_id = #{oId}


    and di.depot_id in

        #{item}



    and dh.type=#{type}


    and (dh.sub_type!='采购' and dh.sub_type!='采购退货' and dh.sub_type!='销售' and dh.sub_type!='销售退货')


    and dh.creator in (

        #{creator}

    )


    and (dh.organ_id in (

        #{organId}

    ) or dh.sub_type='采购退货' or dh.sub_type='零售' or dh.sub_type='其它')


    and dh.status = '1'



    and dh.number like #{bindNumber}



    and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})


    and dh.creator=#{creator}



    and (dh.remark like #{bindRemark} or di.remark like #{bindRemark})

and dh.sub_type!='调拨'
and ifnull(dh.delete_flag,'0') !='1'

    order by oper_time desc,number desc


    order by ${column} ${order}


    limit #{offset},#{rows}

