select di.material_id materialId, me.bar_code barCode, m.name mName,m.model,m.standard,mc.name categoryName,concat_ws('', m.unit, u.basic_unit) materialUnit,
sum(di.basic_number) numSum,
sum(di.all_price) priceSum
from t_depot_head dh
left join t_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1'
left join t_material m on m.id=di.material_id and ifnull(m.delete_flag,'0') !='1'
left join t_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1'
left join t_material_extend me on me.material_id=m.id and ifnull(me.delete_Flag,'0') !='1'
left join t_material_category mc on m.category_id=mc.Id and ifnull(mc.delete_flag,'0') !='1'
where me.default_flag=1
and (dh.type=#{type} or dh.sub_type='调拨') and dh.oper_time >=#{beginTime} and dh.oper_time <=#{endTime}

    and dh.organ_id = #{oId}


    and dh.creator in (

        #{creator}

    )


    and (dh.organ_id in (

        #{organId}

    ) or dh.sub_type='采购退货' or dh.sub_type='零售' or dh.sub_type='其它')



        and ((
        dh.sub_type!='调拨' and di.depot_id in

            #{item}

        ) or (
        dh.sub_type='调拨' and di.another_depot_id in

            #{item}

        ))


        and di.depot_id in

            #{item}





    and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})


    and (dh.status = '1' or dh.status = '2' or dh.status = '3')


    and (dh.sub_type!='采购' and dh.sub_type!='采购退货' and dh.sub_type!='销售' and dh.sub_type!='销售退货')

and ifnull(dh.delete_flag,'0') !='1'
group by di.material_id

    order by materialId desc


    order by ${column} ${order}


    limit #{offset},#{rows}

