select distinct dh.*, s.supplier OrganName, u.username userName
from t_depot_head dh
left join t_supplier s on dh.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1'
left join t_user u on dh.creator=u.id and ifnull(u.Status,'0') !='1'
left join t_depot_item di on dh.id = di.header_id and ifnull(di.delete_flag,'0') !='1'
left join t_material m on di.material_id = m.id and ifnull(m.delete_flag,'0') !='1'
where 1=1
and (dh.type='入库' or dh.type='出库')
and (dh.sub_type='采购' or dh.sub_type='销售' or dh.sub_type='采购退货' or dh.sub_type='销售退货')

    and dh.organ_id = #{organId}


    and dh.status =#{status}



    and dh.number like #{bindNumber}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}



    and (m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})


    and di.depot_id in (

        #{depotId}

    )


    and dh.creator in (

        #{creator}

    )

and (abs(dh.change_amount)+(select ifnull(sum(abs(ai.each_amount)),0) from u_account_item ai where ai.bill_id=dh.id and ifnull(ai.delete_flag,'0') !='1'))
< (dh.discount_last_money + dh.other_money - ifnull(dh.deposit,0))
and ifnull(dh.delete_flag,'0') !='1'
order by dh.oper_time desc

    limit #{offset},#{rows}

